import React, { useState } from 'react';
import axios from 'axios';

function PhishingChecker() {
  const [inputText, setInputText] = useState("");
  const [analysisResult, setAnalysisResult] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleAnalyze = () => {
    setIsLoading(true);
    setAnalysisResult("");  // clear previous result before new request

    // IMPORTANT: Make sure this endpoint matches your Flask backend
    // e.g. "http://127.0.0.1:5000/detect_phishing" if your backend route is /detect_phishing
    axios.post("http://127.0.0.1:5000/detect_phishing", { text: inputText })
      .then((response) => {
        setIsLoading(false);
        if (response.data.analysis) {
          setAnalysisResult(response.data.analysis);
        } else {
          setAnalysisResult("No analysis received.");
        }
      })
      .catch((error) => {
        setIsLoading(false);
        setAnalysisResult(`Error: ${error.message}`);
      });
  };

  return (
    <div className="container">
      <h2>Phishing Checker</h2>
      <textarea
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
        placeholder="Enter URL, email, or text here..."
      />
      <br />
      <button onClick={handleAnalyze}>Analyze</button>

      {/* Optional LOADING spinner */}
      {isLoading && <div className="spinner"></div>}

      {/* Animate the result with .result */}
      {analysisResult && <div className="result">{analysisResult}</div>}
    </div>
  );
}

export default PhishingChecker;
