// src/App.js
import React from 'react';
import './App.css';     // Basic resets or global styles
import './Layout.css';  // The Discord-like layout & theme

import Sidebar from './components/Sidebar';
import ChatWindow from './components/ChatWindow';
import RightPanel from './components/RightPanel';

function App() {
  return (
    <div className="appContainer">
      {/* Left Sidebar */}
      <Sidebar />

      {/* Main Chat Window */}
      <ChatWindow />

      {/* Right Panel for media/files/etc */}
      <RightPanel />
    </div>
  );
}

export default App;
