// src/components/ChatWindow.js
import React from 'react';
import ChatHeader from './ChatHeader';
import ChatInput from './ChatInput';

function ChatWindow() {
  // In a real app, you’d store messages in state or Redux, etc.
  // Here we’re just hardcoding some placeholders.
  const messages = [
    { id: 1, author: "Oliver", text: "Hey there, check out my new design." },
    { id: 2, author: "Jeff", text: "Wow, that’s pretty awesome!" },
    { id: 3, author: "You", text: "Testing the phishing checker here..." },
  ];

  return (
    <div className="chatWindow">
      <ChatHeader />

      {/* Scrollable message list */}
      <div className="chatMessages">
        {messages.map((msg) => (
          <div key={msg.id} className="messageBubble">
            <strong>{msg.author}:</strong> {msg.text}
          </div>
        ))}
      </div>

      {/* Input area */}
      <ChatInput />
    </div>
  );
}

export default ChatWindow;
