import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import axios from "axios"; 

function App() {
  // State for the user‚Äôs input
  const [userInput, setUserInput] = useState("");

  // State for the analysis result to display
  const [analysisResult, setAnalysisResult] = useState("");

  // Ref for the auto-resizing text area
  const textAreaRef = useRef(null);

  // Automatically resize the text area as user types
  useEffect(() => {
    if (textAreaRef.current) {
      textAreaRef.current.style.height = "auto"; 
      textAreaRef.current.style.height = textAreaRef.current.scrollHeight + "px";
    }
  }, [userInput]);

  // Called when the user clicks "Analyze"
  const handleAnalyze = () => {
    axios
      .get("https://safenet-backend-eyhx.onrender.com/")
      .then((response) => {
        console.log("API Response:", response.data);
        setMessage(response.data.message);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setMessage("Error connecting to backend");
      });
  }

  return (
    <div className="gradient-background">
      {/* Logo Placeholder */}
      <div className="logo-placeholder">LOGO</div>

      {/* Main Title & Subtitle */}
      <h1 className="main-title">SafeNet</h1>
      <p className="subtitle">A brief description of what this tool does...</p>

      {/* Categories Section - purely visual now */}
      <div className="categories-container">
        <div className="category-card">
          <span className="category-icon">‚úâÔ∏è</span>
          <h3>Emails</h3>
          <p>Analyze email addresses</p>
        </div>

        <div className="category-card">
          <span className="category-icon">üîó</span>
          <h3>URLs</h3>
          <p>Check website links</p>
        </div>

        <div className="category-card">
          <span className="category-icon">üìù</span>
          <h3>Email Contents</h3>
          <p>Review suspicious text</p>
        </div>
      </div>

      {/* Auto-resizing Text Box */}
      <textarea
        ref={textAreaRef}
        className="auto-textarea"
        placeholder="Enter your text here..."
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
      />

      {/* Analyze Button */}
      <button className="analyze-button" onClick={handleAnalyze}>
        Analyze
      </button>

      {/* Result Display */}
      <div className="result-area">
        {analysisResult && <p>{analysisResult}</p>}
      </div>
    </div>
  );
}

export default App;
