import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import axios from "axios"; 
import PhishingChecker from "./PhishingChecker"; 

function App() {
  // State for user input
  const [userInput, setUserInput] = useState("");

  // State for displaying backend analysis
  const [analysisResult, setAnalysisResult] = useState("");

  // Loading state for API request
  const [isLoading, setIsLoading] = useState(false);

  // Ref for the auto-resizing text area
  const textAreaRef = useRef(null);
  // Automatically resize the text area as user types
  useEffect(() => {
    if (textAreaRef.current) {
      textAreaRef.current.style.height = "auto"; 
      textAreaRef.current.style.height = textAreaRef.current.scrollHeight + "px";
    }
  }, [userInput]);



  // Called when user clicks "Analyze"
  const handleAnalyze = () => {
    if (!userInput.trim()) {
      setAnalysisResult({ text: "Please enter text before analyzing.", type: "default" });
      return;
    }

    setIsLoading(true);
    setAnalysisResult(""); // Clear previous result

    axios
      .post("http://127.0.0.1:5000/detect_phishing", { text: userInput })
      .then((response) => {
        setIsLoading(false);

        if (response.data.analysis) {
          // Extract classification from the analysis response
          let resultText = response.data.analysis;
          let resultType = "default"; // Default styling if classification is unclear

          if (resultText.includes("Safe")) {
            resultType = "safe";
          } else if (resultText.includes("Suspicious")) {
            resultType = "suspicious";
          } else if (resultText.includes("Danger")) {
            resultType = "danger";
          }

          setAnalysisResult({ text: resultText, type: resultType });
        } else {
          setAnalysisResult({ text: "No analysis received.", type: "default" });
        }
      })
      .catch((error) => {
        setIsLoading(false);
        setAnalysisResult({ text: `Error: ${error.message}`, type: "default" });
      });
  };



  return (
    <div className="gradient-background">
      {/* Logo Placeholder */}
      <div className="logo-placeholder">LOGO</div>

      {/* Main Title & Subtitle */}
      <h1 className="main-title">SafeNet</h1>
      <p className="subtitle">A brief description of what this tool does...</p>

      {/* Categories Section - purely visual now */}
      <div className="categories-container">
        <div className="category-card">
          <span className="category-icon">‚úâÔ∏è</span>
          <h3>Emails</h3>
          <p>Analyze email addresses</p>
        </div>

        <div className="category-card">
          <span className="category-icon">üîó</span>
          <h3>URLs</h3>
          <p>Check website links</p>
        </div>

        <div className="category-card">
          <span className="category-icon">üìù</span>
          <h3>Email Contents</h3>
          <p>Review suspicious text</p>
        </div>
      </div>

      {/* Auto-resizing Text Box */}
      <textarea
        ref={textAreaRef}
        className="auto-textarea"
        placeholder="Enter your text here..."
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
      />

      {/* Analyze Button */}
      <button className="analyze-button" onClick={handleAnalyze}>
        Analyze
      </button>

      {/* Result Display */}
      <div className="result-area">
        {analysisResult && <p>{analysisResult}</p>}
      </div>
    </div>
  );
}

export default App;
