import React, { useState, useEffect, useRef } from "react";
import "./App.css";

function App() {
  // State for which category is selected (Emails, URLs, Email Contents)
  const [selectedCategory, setSelectedCategory] = useState("");

  // State for the user‚Äôs input
  const [userInput, setUserInput] = useState("");

  // State for the analysis result to display
  const [analysisResult, setAnalysisResult] = useState("");

  // Ref for the auto-resizing text area
  const textAreaRef = useRef(null);

  // Automatically resize the text area as user types
  useEffect(() => {
    if (textAreaRef.current) {
      textAreaRef.current.style.height = "auto"; // reset
      textAreaRef.current.style.height = textAreaRef.current.scrollHeight + "px";
    }
  }, [userInput]);

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  const handleAnalyze = () => {
    // Here you‚Äôd call your backend or do local analysis
    // For demonstration, we‚Äôll just set a dummy result:
    setAnalysisResult(`Analyzing "${userInput}" under [${selectedCategory}] category...`);
  };

  return (
    <div className="gradient-background">
      {/* Logo Placeholder */}
      <div className="logo-placeholder">LOGO</div>

      {/* Main Title & Subtitle */}
      <h1 className="main-title">SafeNet</h1>
      <p className="subtitle">A brief description of what this tool does...</p>

      {/* Categories Section */}
      <div className="categories-container">
        <div
          className={`category-card ${selectedCategory === "emails" ? "active-card" : ""}`}
          onClick={() => handleCategoryClick("emails")}
        >
          <span className="category-icon">‚úâÔ∏è</span>
          <h3>Emails</h3>
          <p>Analyze email addresses</p>
        </div>

        <div
          className={`category-card ${selectedCategory === "urls" ? "active-card" : ""}`}
          onClick={() => handleCategoryClick("urls")}
        >
          <span className="category-icon">üîó</span>
          <h3>URLs</h3>
          <p>Check website links</p>
        </div>

        <div
          className={`category-card ${selectedCategory === "contents" ? "active-card" : ""}`}
          onClick={() => handleCategoryClick("contents")}
        >
          <span className="category-icon">üìù</span>
          <h3>Email Contents</h3>
          <p>Review suspicious text</p>
        </div>
      </div>

      {/* Auto-resizing Text Box */}
      <textarea
        ref={textAreaRef}
        className="auto-textarea"
        placeholder="Enter your text here..."
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
      />

      {/* Analyze Button */}
      <button className="analyze-button" onClick={handleAnalyze}>
        Analyze
      </button>

      {/* Result Display */}
      <div className="result-area">
        {analysisResult && <p>{analysisResult}</p>}
      </div>
    </div>
  );
}

export default App;
