import React, { useState } from "react";
import axios from "axios";

function PhishingChecker() {
  const [inputText, setInputText] = useState("");
  const [analysisResult, setAnalysisResult] = useState("");

  const handleAnalyze = () => {
    axios.post("https://safenet-backend-eyhx.onrender.com/detect_phishing", { text: inputText })
      .then((response) => {
        if (response.data.analysis) {
          setAnalysisResult(response.data.analysis);
        } else if (response.data.error) {
          setAnalysisResult("Error: " + response.data.error);
        } else {
          setAnalysisResult("No analysis received");
        }
      })
      .catch((error) => {
        console.error(error);
        setAnalysisResult("Error analyzing text");
      });
  };

  return (
    <div className="container">
      <h2>Phishing Checker</h2>
      <textarea
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
        placeholder="Enter URL, email, or text here"
      />
      <br />
      <button onClick={handleAnalyze}>Analyze</button>
      {analysisResult && <div className="result">{analysisResult}</div>}
    </div>
  );
}

export default PhishingChecker;
